import React, { useState } from "react";
import AddField from "./components/AddField";
import axios from "axios";

const Fields = () => {
  const [fields, setFields] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedField, setSelectedField] = useState(null);
  const [subfieldName, setSubfieldName] = useState("");

  const editField = (field) => {
    const fieldWithSubfields = {
      ...field,
      subfields: field.subfields || [],
    };
    setSelectedField(fieldWithSubfields);
    setIsModalOpen(true);
  };

  const addSubfield = () => {
    if (!selectedField) return;
    const newSubfield = {
      id: Date.now(),
      name: subfieldName || "New Subfield",
    };
    const updatedField = {
      ...selectedField,
      subfields: [...selectedField.subfields, newSubfield],
    };
    setSelectedField(updatedField);
    setSubfieldName("");
  };


  // Single API call here to add a field
  const addFieldToApi = async (field) => {
    try {
      const response = await axios.post("http://localhost:8080/api/add-field", {
        name: field.name,
        description: field.description || "No description",
        address: field.streetAddress,
        zip_code: field.zipCode,
        city: field.city,
      });
      if (response.status === 201) {
        setFields([...fields, response.data]);
      }
    } catch (error) {
      console.error("Error adding field:", error);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-4">
      <h1 className="text-2xl font-bold mb-4 text-green-600">Fields Page</h1>

      {/* Add new field using the AddField component */}
      <AddField
        onSendField={addFieldToApi} // Pass addFieldToApi function directly
        lastId={fields.reduce(
          (prev, curr) => (curr.id > prev ? curr.id : prev),
          0
        )}
      />

      {/* Display fields in a table */}
      {fields.length > 0 && (
        <div className="relative overflow-x-auto shadow-md sm:rounded-lg mt-4">
          <table className="w-full text-sm text-left text-gray-500">
            <thead className="text-xs text-gray-700 uppercase bg-green-100">
              <tr>
                <th scope="col" className="px-6 py-3">
                  Field Name
                </th>
                <th scope="col" className="px-6 py-3">
                  Street Address
                </th>
                <th scope="col" className="px-6 py-3">
                  Zip Code
                </th>
                <th scope="col" className="px-6 py-3">
                  City
                </th>
                <th scope="col" className="px-6 py-3">
                  Actions
                </th>
              </tr>
            </thead>
            <tbody>
              {fields.map((field) => (
                <tr key={field.id} className="bg-white border-b">
                  <th
                    scope="row"
                    className="px-6 py-4 font-medium text-gray-900 whitespace-nowrap"
                  >
                    {field.name}
                  </th>
                  <td className="px-6 py-4">{field.streetAddress}</td>
                  <td className="px-6 py-4">{field.zipCode}</td>
                  <td className="px-6 py-4">{field.city}</td>
                  <td className="px-6 py-4 text-right">
                    <button
                      onClick={() => editField(field)}
                      className="font-medium text-green-600 hover:underline"
                    >
                      Edit
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default Fields;
